import React from 'react';

const AttendanceTable = ({ attendanceData }) => {
    const dates = Object.keys(attendanceData); // Array of dates
    const students = Object.keys(attendanceData[dates[0]]); // Array of student names

    return (
        <div className="attendance-container">
            <h2>Day-wise Attendance</h2>
            <table className="attendance-table">
                <thead>
                    <tr>
                        <th>Student</th>
                        {dates.map((date) => (
                            <th key={date}>{date}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {students.map((student) => (
                        <tr key={student}>
                            <td>{student}</td>
                            {dates.map((date) => (
                                <td key={date} className={attendanceData[date][student] === "Present" ? "present" : "absent"}>
                                    {attendanceData[date][student]}
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AttendanceTable;
