import React, { useEffect, useState } from 'react';
import './AttendanceTable.css';

const AttendanceTable = () => {
  const [attendanceData, setAttendanceData] = useState([]);
  const [isAttendanceReady, setIsAttendanceReady] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // Function to handle file upload
  const handleFileUpload = (file) => {
    const formData = new FormData();
    formData.append('file', file);

    setIsLoading(true);
    fetch('http://localhost:3001/upload', {
      method: 'POST',
      body: formData,
    })
      .then((response) => response.json())
      .then(() => {
        setIsAttendanceReady(true);
      })
      .catch((error) => {
        console.error('Error uploading file:', error);
        setIsAttendanceReady(false);
      })
      .finally(() => setIsLoading(false));
  };

  // Function to capture a photo
  const handleCapturePhoto = () => {
    console.log('Capture Photo button clicked'); // Debug log
    setIsLoading(true);
    fetch('http://localhost:3001/capture')
      .then((response) => response.json())
      .then(() => {
        setIsAttendanceReady(true);
      })
      .catch((error) => {
        console.error('Error capturing photo:', error);
        setIsAttendanceReady(false);
      })
      .finally(() => setIsLoading(false));
  };

  useEffect(() => {
    if (isAttendanceReady) {
      setIsLoading(true);
      fetch(`http://localhost:3001/api/attendance`)
        .then((response) => response.json())
        .then((data) => {
          setAttendanceData(data.attendance);
        })
        .catch((error) => {
          console.error('Error fetching attendance data:', error);
        })
        .finally(() => setIsLoading(false));
    }
  }, [isAttendanceReady]);

  // Function to render the table header
  const renderTableHeader = () => {
    const headers = ["Name", ...new Set(attendanceData.map((item) => item.date))];
    return (
      <tr>
        {headers.map((header, index) => (
          <th key={index}>{header}</th>
        ))}
      </tr>
    );
  };

  // Function to render the table rows
  const renderTableRows = () => {
    const names = [...new Set(attendanceData.map((item) => item.name))];
    return names.map((name, rowIndex) => {
      const row = attendanceData.filter((item) => item.name === name);
      return (
        <tr key={rowIndex}>
          <td>{name}</td>
          {row.map((entry, colIndex) => (
            <td key={colIndex}>{entry.present ? "Present" : "Absent"}</td>
          ))}
        </tr>
      );
    });
  };

  return (
    <div className="attendance-container">
      <h2>Day-wise Attendance</h2>
      <input
        type="file"
        onChange={(e) => handleFileUpload(e.target.files[0])}
        disabled={isLoading}
      />
      <button onClick={handleCapturePhoto} disabled={isLoading}>
        Capture Photo
      </button>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <table className="attendance-table">
          <thead>{attendanceData.length > 0 && renderTableHeader()}</thead>
          <tbody>
            {attendanceData.length > 0 ? (
              renderTableRows()
            ) : (
              <tr>
                <td colSpan="100%">No attendance data available</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AttendanceTable;
