// src/components/Navbar.js
import React, { useState } from "react";
import axios from "axios";
import './Navbar.css';

const Navbar = () => {
    const [image, setImage] = useState(null);

    // Handle file selection
    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    // Handle file upload
    const handleUpload = async () => {
        if (!image) {
            alert("Please select an image first");
            return;
        }

        const formData = new FormData();
        formData.append('file', image);

        try {
            // Send image to the backend using axios
            const res = await axios.post('http://localhost:3001/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            console.log('Response:', res.data); // Show result (recognized names)
            alert(`Recognized faces: ${res.data.names.join(', ')}`);
        } catch (err) {
            console.error("Error uploading the image", err);
            alert('Error in face recognition');
        }
    };

    return (
        <>
            <nav className="navbar">
                <div className="navbar-content">
                    <h1>Attenue</h1>
                    <p>Your Smart Attendance Dashboard</p>
                </div>
            </nav>
            <div className="content">
                <h2>Upload Image for Attendance Recognition</h2>
                <div className="add-image">
                    <input type="file" onChange={handleImageChange} />
                    <button onClick={handleUpload}>Upload Image</button>
                </div>
                <div className="attendance">
                    <h2>Day-wise Attendance</h2>
                    {/* Only display attendance data here when available */}
                    <p>No attendance data available</p>
                </div>
            </div>
        </>
    );
};

export default Navbar;
